mapper.update:
update restaurant
rt_num = #{rtNum}, rt_term = #{rtTerm}, rt_field = #{rtField}, rt_holiday = #{rtHoliday}, rt_name = #{rtName}, rt_tel = #{rtTel}, rt_open = to_date(#{rtOpen}, 'hh24 : mi'), rt_close = to_date(#{rtClose},'hh24 : mi'), rt_picture = #{rtPicture}, rt_address = #{rtAddress}, rt_capacity = #{rtCapacity}, rt_deposit = #{rtDeposit}
where business_id = #{businessId}
